<?php

namespace App\Controllers;

use App\Renderable;
use App\Tasks\SecondTask;
use App\View;

/**
 * Контроллер для второго задания
 */
class AdditionBigNumbersController extends Controller
{
    /**
     * @return View
     */
    public function index(): Renderable
    {
        $firstNumber  = '';
        $secondNumber = '';

        $secondTask = new SecondTask();
        $sum        = $secondTask->makeSumTwoBigNumbers($firstNumber, $secondNumber);

        return new View('tasks.second_task', [
            'firstNumber'  => $firstNumber,
            'secondNumber' => $secondNumber,
            'totalSum'     => $sum
        ]);
    }
}
